  width: 100%;
  height: 10px;
  background-color: #333333;
  border-radius: 5px;
  overflow: hidden;
  box-shadow: inset 0 2px 4px rgba(0, 0, 0, 0.5);
}

.loadingProgress {
  height: 100%;
  background: linear-gradient(to right, #0066ff, #0099ff, #0066ff);
  transition: width 0.3s ease;
