            title="My Computer"
            img={mycomputer}
          />
          <DesktopIcon
            appID={2}
            doubleClick={() => void 0}
            title="Recycling Bin"
            img={bin}
          />
          <DesktopIcon
            appID={3}
            doubleClick={handleOpenResume}
            title="My Resume"
            img={pdf}
          />
          <DesktopIcon
            appID={4}
            doubleClick={handleOpenLinkedin}
            title="My LinkedIn"
            img={linkedin}
          />
          <DesktopIcon
            appID={5}
            doubleClick={() => handleRunApp(2)}
            title="My Work"
            img={myworkicon}
          />

          <DesktopIcon
            appID={6}
            doubleClick={() => handleRunApp(4)}
            title="My Hobbies"
            img={solitare}
          />
          {Tabs.map((tab, index) => {
            return tab.isMinimized ? (
              <></>
